mapper.select:
select
  uuid, user_id, product_id, point_number, context, is_delete, modify_time, create_time
from cloudcity.tb_product_review


        and uuid = #{uuid}


        and user_id = #{userId}


        and product_id = #{productId}


        and point_number = #{pointNumber}


        and context = #{context}


        and is_delete = #{isDelete}


        and modify_time = #{modifyTime}


        and create_time = #{createTime}
